<?php/** * Elementor Widget * @package wphex * @since 1.0.0 */namespace Elementor;class wphex_About_US_Widget extends Widget_Base {    /**     * Get widget name.     *     * Retrieve Elementor widget name.     *     * @return string Widget name.     * @since 1.0.0     * @access public     *     */    public function get_name() {        return 'wphex-about-us-widget';    }    /**     * Get widget title.     *     * Retrieve Elementor widget title.     *     * @return string Widget title.     * @since 1.0.0     * @access public     *     */    public function get_title() {        return esc_html__( 'wphex About Us', 'wphex-master' );    }    /**     * Get widget icon.     *     * Retrieve Elementor widget icon.     *     * @return string Widget icon.     * @since 1.0.0     * @access public     *     */    public function get_icon() {        return 'eicon-wordpress';    }    /**     * Get widget categories.     *     * Retrieve the list of categories the Elementor widget belongs to.     *     * @return array Widget categories.     * @since 1.0.0     * @access public     *     */    public function get_categories() {        return [ 'wordpress' ];    }    /**     * Register Elementor widget controls.     *     * Adds different input fields to allow the user to change and customize the widget settings.     *     * @since 1.0.0     * @access protected     */    protected function _register_controls() {        $this->start_controls_section(            'about_us_content',            [                'label' => esc_html__( 'About Us Content', 'wphex-master' ),                'tab'   => Controls_Manager::TAB_CONTENT,            ]        );        $this->add_control(            'image',            [                'label' => esc_html__( 'Choose Logo', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::MEDIA,                'default' => [                    'url' => \Elementor\Utils::get_placeholder_image_src(),                ],            ]        );        $this->add_control(            'description',            [                'label' => esc_html__( 'Description', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::TEXTAREA,                'default' => esc_html__( 'Amet minim mollit non deserunt ullamco est sit ali dolor do amet sint.', 'wphex-master' ),                'placeholder' => esc_html__( 'Type your description here', 'wphex-master' ),            ]        );        $this->add_control(            'social_list',            [                'label' => esc_html__( 'Social List', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::REPEATER,                'fields' => [                    [                        'name' => 'social_icon',                        'label' => esc_html__( 'Icon', 'wphex-master' ),                        'type' => \Elementor\Controls_Manager::ICONS,                        'default' => [                            'value' => 'fab fa-facebook-f',                            'library' => 'fa-solid',                        ],                    ],                    [                        'name' => 'social_link',                        'label' => esc_html__( 'Content', 'wphex-master' ),                        'type' => \Elementor\Controls_Manager::URL,                        'placeholder' => esc_html__( 'https://your-link.com', 'wphex-master' ),                        'options' => [ 'url', 'is_external', 'nofollow' ],                        'default' => [                            'url' => '#',                            'is_external' => true,                            'nofollow' => true,                            // 'custom_attributes' => '',                        ],                        'label_block' => true,                    ],                ],            ]        );        $this->end_controls_section();    }    /**     * Render Elementor widget output on the frontend.     *     * Written in PHP and used to generate the final HTML.     *     * @since 1.0.0     * @access protected     */    protected function render() {        $settings = $this->get_settings_for_display();        $image = ! empty( $settings['image']['url'] ) ? $settings['image']['url'] : [];//        $alt_text = $this->add_render_attribute( 'image', 'alt', \Elementor\Control_Media::get_image_alt( $settings['image'] ) );        ?>        <div class="footer-contents-logo">            <a href="index.html" class="footer-contents-logo-img"> <img src="<?php echo esc_url( $image ); ?>" alt=""> </a>        </div>        <div class="footer-widget-inner mt-4">            <p class="footer-widget-para"> <?php echo esc_html( $settings['description'] ); ?> </p>            <div class="footer-widget-social mt-4">                <ul class="footer-widget-social-list gradient-one list-style-none">                    <?php foreach( $settings['social_list'] as $single_item ) : ?>                    <li class="footer-widget-social-list-item">                        <a class="footer-widget-social-list-link" href="<?php echo esc_url( $single_item['social_link']['url'] ); ?>"> <?php \Elementor\Icons_Manager::render_icon( $single_item['social_icon'], [ 'aria-hidden' => 'true' ] ); ?> </a>                    </li>                    <?php endforeach; ?>                </ul>            </div>        </div>        <?php    }}Plugin::instance()->widgets_manager->register_widget_type( new wphex_About_US_Widget() );