<?php/** * Elementor Widget * @package wphex * @since 1.0.0 */namespace Elementor;class wphex_Advanced_Product_Search_Widget extends Widget_Base {    /**     * Get widget name.     *     * Retrieve Elementor widget name.     *     * @return string Widget name.     * @since 1.0.0     * @access public     *     */    public function get_name() {        return 'wphex-advanced-product-search-widget';    }    /**     * Get widget title.     *     * Retrieve Elementor widget title.     *     * @return string Widget title.     * @since 1.0.0     * @access public     *     */    public function get_title() {        return esc_html__( 'wphex Advanced Product Search', 'wphex-master' );    }    /**     * Get widget icon.     *     * Retrieve Elementor widget icon.     *     * @return string Widget icon.     * @since 1.0.0     * @access public     *     */    public function get_icon() {        return 'eicon-wordpress';    }    /**     * Get widget categories.     *     * Retrieve the list of categories the Elementor widget belongs to.     *     * @return array Widget categories.     * @since 1.0.0     * @access public     *     */    public function get_categories() {        return [ 'wordpress' ];    }    /**     * Register Elementor widget controls.     *     * Adds different input fields to allow the user to change and customize the widget settings.     *     * @since 1.0.0     * @access protected     */    protected function _register_controls() {        $this->start_controls_section(            'search_content',            [                'label' => esc_html__( 'Product Search Content', 'wphex-master' ),                'tab'   => Controls_Manager::TAB_CONTENT,            ]        );        $this->end_controls_section();    }    /**     * Render Elementor widget output on the frontend.     *     * Written in PHP and used to generate the final HTML.     *     * @since 1.0.0     * @access protected     */    protected function render() {        $settings = $this->get_settings_for_display();        $args = array(            'taxonomy' => 'download_category',            'orderby' => 'name',            'order'   => 'ASC'        );        $cats = get_categories($args);        ?>        <div class="product_search__left">            <div class="product_search_select">                <select id="mySelect" name="mySelect[]" class="js_nice_select">                    <?php foreach ( $cats as $cat ) : ?>                    <option data-display="<?php  echo esc_html( $cat->name ); ?>"><?php  echo esc_html( $cat->name ); ?></option>                    <?php endforeach; ?>                </select>            </div>            <div id="output">                <ul class="product_search__list">                </ul>            </div>        </div><!--        <div id="output">--><!--            <ul></ul>--><!--        </div>-->        <?php    }}Plugin::instance()->widgets_manager->register_widget_type( new wphex_Advanced_Product_Search_Widget() );