<?php/** * Elementor Widget * @package wphex * @since 1.0.0 */namespace Elementor;class wphex_Breadcrumb extends Widget_Base {    /**     * Get widget name.     *     * Retrieve Elementor widget name.     *     * @return string Widget name.     * @since 1.0.0     * @access public     *     */    public function get_name() {        return 'wphex-breadcrumb-widget';    }    /**     * Get widget title.     *     * Retrieve Elementor widget title.     *     * @return string Widget title.     * @since 1.0.0     * @access public     *     */    public function get_title() {        return esc_html__( 'wphex Breadcrumb', 'wphex-master' );    }    /**     * Get widget icon.     *     * Retrieve Elementor widget icon.     *     * @return string Widget icon.     * @since 1.0.0     * @access public     *     */    public function get_icon() {        return 'eicon-product-breadcrumbs';    }    /**     * Get widget categories.     *     * Retrieve the list of categories the Elementor widget belongs to.     *     * @return array Widget categories.     * @since 1.0.0     * @access public     *     */    public function get_categories() {        return [ 'wphex_widgets' ];    }    /**     * Register Elementor widget controls.     *     * Adds different input fields to allow the user to change and customize the widget settings.     *     * @since 1.0.0     * @access protected     */    protected function _register_controls() {        $this->start_controls_section(            'breadcrumb_content',            [                'label' => esc_html__( 'Breadcumb Content', 'wphex-master' ),                'tab'   => Controls_Manager::TAB_CONTENT,            ]        );        $this->add_control(            'page_title',            [                'label' => esc_html__( 'Page Title', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::TEXT,                'default' => esc_html__( 'Home', 'wphex-master' ),                'placeholder' => esc_html__( 'Type your title here', 'wphex-master' ),            ]        );        $this->add_control(            'breadcrumb_list',            [                'label' => esc_html__( 'Breadcrumb List', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::REPEATER,                'fields' => [                    [                        'name' => 'menu_title',                        'label' => esc_html__( 'Title', 'wphex-master' ),                        'type' => \Elementor\Controls_Manager::TEXT,                        'default' => esc_html__( 'Menu Title' , 'wphex-master' ),                        'label_block' => true,                    ],                    [                        'name' => 'enable_link',                        'label' => esc_html__( 'Enable Link', 'wphex-master' ),                        'type' => \Elementor\Controls_Manager::SWITCHER,                        'label_on' => esc_html__( 'Show', 'wphex-master' ),                        'label_off' => esc_html__( 'Hide', 'wphex-master' ),                        'return_value' => 'yes',                        'default' => 'yes',                    ],                    [                        'name' => 'menu_link',                        'label' => esc_html__( 'Menu Link', 'wphex-master' ),                        'type' => \Elementor\Controls_Manager::URL,                        'options' => [ 'url', 'is_external', 'nofollow' ],                        'default' => [                            'url' => '',                            'is_external' => true,                            'nofollow' => true,                            // 'custom_attributes' => '',                        ],                        'label_block' => true,                        'condition' => [                                'enable_link' => 'yes'                        ]                    ],                ],                'default' => [                    [                        'menu_title' => esc_html__( 'Menu Title', 'wphex-master' ),                        'menu_link' => [                                'url' => '',                        ],                        'enable_link' => 'yes',                    ],                ],            ]        );        $this->end_controls_section();    }    /**     * Render Elementor widget output on the frontend.     *     * Written in PHP and used to generate the final HTML.     *     * @since 1.0.0     * @access protected     */    protected function render() {        $settings = $this->get_settings_for_display();        ?>        <div class="breadcrumb__contents center-text">            <h2 class="breadcrumb__title"> <?php echo esc_html( $settings['page_title'] ); ?> </h2>            <ul class="breadcrumb__list">                <?php if( ! empty( $settings['breadcrumb_list'] ) ) : foreach( $settings['breadcrumb_list'] as $single_item ) : ?>                <li class="breadcrumb__list__item">                    <a href="<?php if( ! empty( $single_item['menu_link']['url'] ) ) { echo esc_url( $single_item['menu_link']['url'] ); } else { echo esc_html( 'javascript:void(0)' ); }?>" class="breadcrumb__list__link"><?php echo esc_html( $single_item['menu_title'] ); ?></a>                </li>                <?php endforeach; endif; ?>            </ul>        </div>        <?php    }}Plugin::instance()->widgets_manager->register_widget_type( new wphex_Breadcrumb() );