<?php/** * Elementor Widget * @package wphex * @since 1.0.0 */namespace Elementor;class wphex_Contact_Info_Two_Widget extends Widget_Base {    /**     * Get widget name.     *     * Retrieve Elementor widget name.     *     * @return string Widget name.     * @since 1.0.0     * @access public     *     */    public function get_name() {        return 'wphex-contact-info-two-widget';    }    /**     * Get widget title.     *     * Retrieve Elementor widget title.     *     * @return string Widget title.     * @since 1.0.0     * @access public     *     */    public function get_title() {        return esc_html__( 'wphex Contact Info Two', 'wphex-master' );    }    /**     * Get widget icon.     *     * Retrieve Elementor widget icon.     *     * @return string Widget icon.     * @since 1.0.0     * @access public     *     */    public function get_icon() {        return 'eicon-wordpress';    }    /**     * Get widget categories.     *     * Retrieve the list of categories the Elementor widget belongs to.     *     * @return array Widget categories.     * @since 1.0.0     * @access public     *     */    public function get_categories() {        return [ 'wordpress' ];    }    /**     * Register Elementor widget controls.     *     * Adds different input fields to allow the user to change and customize the widget settings.     *     * @since 1.0.0     * @access protected     */    protected function _register_controls() {        $this->start_controls_section(            'content',            [                'label' => esc_html__( 'Contact Info Content', 'wphex-master' ),                'tab'   => Controls_Manager::TAB_CONTENT,            ]        );        $this->add_control(            'title',            [                'label' => esc_html__( 'Title', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::TEXT,                'default' => esc_html__( 'Contact Us', 'wphex-master' ),                'placeholder' => esc_html__( 'Type your title here', 'wphex-master' ),            ]        );        $this->add_control(            'phone',            [                'label' => esc_html__( 'Phone', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::TEXT,                'default' => esc_html__( '(229) 555-0109', 'wphex-master' ),                'placeholder' => esc_html__( 'Type your phone no here', 'wphex-master' ),            ]        );        $this->add_control(            'phone_icon',            [                'label' => esc_html__( 'Phone Icon', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::ICONS,                'separator' => 'after',                'default' => [                    'value' => 'fas fa-phone-alt',                    'library' => 'fa-solid',                ],            ]        );        $this->add_control(            'email',            [                'label' => esc_html__( 'Email', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::TEXT,                'default' => esc_html__( 'info@washeco.co.uk', 'wphex-master' ),                'placeholder' => esc_html__( 'Type your email here', 'wphex-master' ),            ]        );        $this->add_control(            'email_icon',            [                'label' => esc_html__( 'Email Icon', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::ICONS,                'separator' => 'after',                'default' => [                    'value' => 'fas fa-envelope',                    'library' => 'fa-solid',                ],            ]        );        $this->add_control(            'address',            [                'label' => esc_html__( 'Address', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::TEXT,                'default' => esc_html__( '8702 Jayson, Well Suite 348', 'wphex-master' ),                'placeholder' => esc_html__( 'Type your address here', 'wphex-master' ),            ]        );        $this->add_control(            'address_icon',            [                'label' => esc_html__( 'Address Icon', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::ICONS,                'separator' => 'after',                'default' => [                    'value' => 'fas fa-map-marker-alt',                    'library' => 'fa-solid',                ],            ]        );        $this->add_control(            'title_margin_bottom',            [                'label' => esc_html__( 'Title Margin Bottom', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::SLIDER,                'size_units' => [ 'px', 'rem' ],                'range' => [                    'px' => [                        'min' => 0,                        'max' => 1000,                        'step' => 5,                    ],                ],                'default' => [                    'unit' => 'rem',                    'size' => 1.5,                ],                'selectors' => [                    '{{WRAPPER}} .footer-widget-title.contact' => 'margin-bottom: {{SIZE}}{{UNIT}};',                ],            ]        );        $this->end_controls_section();    }    public function all_nav_menus() {        $all_menus = wp_get_nav_menus();        $final_menu = [];        $final_menu[''] = esc_html__( 'Select A Menu','irt' );        foreach( $all_menus as $menu ) {            $final_menu[ $menu->slug ] = $menu->name;        }        return $final_menu;    }    /**     * Render Elementor widget output on the frontend.     *     * Written in PHP and used to generate the final HTML.     *     * @since 1.0.0     * @access protected     */    protected function render() {        $settings = $this->get_settings_for_display();        ?>        <div class="footer-widget widget">            <h4 class="footer-widget-title contact"><?php echo esc_html( $settings['title'] ); ?></h4>            <div class="footer-inner mt-4">                <div class="footer-widget-contact">                    <span class="footer-widget-contact-item"> <span> <i class="fas fa-phone"></i> </span> <a href="tel:<?php echo esc_attr($settings['phone']); ?>"> <?php echo esc_html( $settings['phone'] ); ?> </a> </span>                    <span class="footer-widget-contact-item"> <span> <i class="fas fa-envelope"></i> </span> <a href="mailto:<?php echo esc_attr($settings['email']); ?>"> <?php echo esc_html($settings['email']); ?> </a> </span>                    <span class="footer-widget-contact-item"> <span> <i class="fas fa-map-marker-alt"></i> </span> <?php echo esc_html($settings['address']); ?> </span>                </div>            </div>        </div>        <?php    }}Plugin::instance()->widgets_manager->register_widget_type( new wphex_Contact_Info_Two_Widget() );