<?php/** * Elementor Widget * @package wphex * @since 1.0.0 */namespace Elementor;class wphex_Features_Widget extends Widget_Base{    /**     * Get widget name.     *     * Retrieve Elementor widget name.     *     * @return string Widget name.     * @since 1.0.0     * @access public     *     */    public function get_name()    {        return 'wphex-info-box-two-widget';    }    /**     * Get widget title.     *     * Retrieve Elementor widget title.     *     * @return string Widget title.     * @since 1.0.0     * @access public     *     */    public function get_title()    {        return esc_html__('Feature Box', 'wphex-master');    }    /**     * Get widget icon.     *     * Retrieve Elementor widget icon.     *     * @return string Widget icon.     * @since 1.0.0     * @access public     *     */    public function get_icon()    {        return 'eicon-info-box';    }    /**     * Get widget categories.     *     * Retrieve the list of categories the Elementor widget belongs to.     *     * @return array Widget categories.     * @since 1.0.0     * @access public     *     */    public function get_categories()    {        return ['wphex_widgets'];    }    /**     * Register Elementor widget controls.     *     * Adds different input fields to allow the user to change and customize the widget settings.     *     * @since 1.0.0     * @access protected     */    protected function _register_controls()    {        $this->start_controls_section(            'settings_section',            [                'label' => esc_html__('General Settings', 'wphex-master'),                'tab' => Controls_Manager::TAB_CONTENT,            ]        );        $this->add_control(            'title',            [                'label' => esc_html__('Title', 'wphex-master'),                'type' => Controls_Manager::TEXT,                'description' => esc_html__('enter title.', 'wphex-master'),                'default' => esc_html__('Registration System', 'wphex-master')            ]        );        $this->add_control(            'icon',            [                'label' => esc_html__('Icon', 'wphex-master'),                'type' => Controls_Manager::ICONS,                'description' => esc_html__('select Icon.', 'wphex-master'),                'default' => [                    'value' => 'far fa-user',                    'library' => 'fa-solid',                ],            ]        );        $this->add_control(            'target_link',            [                'label' => esc_html__( 'Link', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::URL,                'placeholder' => esc_html__( 'https://your-link.com', 'wphex-master' ),                'options' => [ 'url', 'is_external', 'nofollow' ],                'default' => [                    'url' => '#',                    'is_external' => true,                    'nofollow' => true,                    // 'custom_attributes' => '',                ],                'label_block' => true,            ]        );        $this->end_controls_section();    }    /**     * Render Elementor widget output on the frontend.     *     * Written in PHP and used to generate the final HTML.     *     * @since 1.0.0     * @access protected     */    protected function render()    {        $settings = $this->get_settings_for_display();        ?><!--        <div id="features" class="features_area section-bg-1 pat-100 pab-100">-->            <div class="single_features center-text single_features__padding radius-10">                <div class="single_features__icon">                    <?php \Elementor\Icons_Manager::render_icon( $settings['icon'], [ 'aria-hidden' => 'true' ] ); ?>                </div>                <div class="single_features__contents mt-4">                    <h4 class="single_features__title"><a href="<?php echo esc_url( $settings['target_link']['url'] ); ?>"><?php echo esc_html( $settings['title'] ); ?></a></h4>                </div>            </div><!--        </div>-->        <?php    }}Plugin::instance()->widgets_manager->register_widget_type(new wphex_Features_Widget());