<?php/** * Elementor Widget * @package Attorg * @since 1.0.0 */namespace Elementor;class wphex_Image_Box_Five_Widget extends Widget_Base {	/**	 * Get widget name.	 *	 * Retrieve Elementor widget name.	 *	 * @since 1.0.0	 * @access public	 *	 * @return string Widget name.	 */	public function get_name() {		return 'wphex-image-box-five-widget';	}	/**	 * Get widget title.	 *	 * Retrieve Elementor widget title.	 *	 * @since 1.0.0	 * @access public	 *	 * @return string Widget title.	 */	public function get_title() {		return esc_html__( 'wphex Image Box', 'wphex-master' );	}	/**	 * Get widget icon.	 *	 * Retrieve Elementor widget icon.	 *	 * @since 1.0.0	 * @access public	 *	 * @return string Widget icon.	 */	public function get_icon() {		return 'eicon-image';	}	/**	 * Get widget categories.	 *	 * Retrieve the list of categories the Elementor widget belongs to.	 *	 * @since 1.0.0	 * @access public	 *	 * @return array Widget categories.	 */	public function get_categories() {		return [ 'wphex_widgets' ];	}	/**	 * Register Elementor widget controls.	 *	 * Adds different input fields to allow the user to change and customize the widget settings.	 *	 * @since 1.0.0	 * @access protected	 */	protected function _register_controls() {		$this->start_controls_section(			'settings_section',			[				'label' => esc_html__( 'General Settings', 'wphex-master' ),				'tab'   => Controls_Manager::TAB_CONTENT,			]		);        $this->add_control(            'style',            [                'label' => esc_html__( 'Style', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::SELECT,                'default' => 'one',                'options' => [                    'one' => esc_html__( 'One', 'wphex-master' ),                    'two'  => esc_html__( 'Two', 'wphex-master' ),                    'three'  => esc_html__( 'Three', 'wphex-master' ),                ],            ]        );		$this->add_control(			'title',			[				'label'       => esc_html__( 'Title', 'wphex-master' ),				'type'        => Controls_Manager::TEXT,				'description' => esc_html__( 'enter title.', 'wphex-master' ),				'default'     => esc_html__('Open a ticket','wphex-master'),                'condition' => [                    'style' => 'one',                ],			]		);		$this->add_control(			'url',			[				'label'       => esc_html__( 'Url', 'wphex-master' ),				'type'        => Controls_Manager::URL,				'description' => esc_html__( 'enter url.', 'wphex-master' ),                'condition' => [                    'style' => 'one',                ],			]		);        $this->add_control(            'image',            [                'label' => esc_html__( 'Choose Image', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::MEDIA,                'default' => [                    'url' => \Elementor\Utils::get_placeholder_image_src(),                ],                'media_types' => [ 'image', 'svg' ],            ]        );        $this->add_control(            'image2',            [                'label' => esc_html__( 'Choose Image Two', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::MEDIA,                'default' => [                    'url' => \Elementor\Utils::get_placeholder_image_src(),                ],                'media_types' => [ 'image', 'svg' ],                'condition' => [                    'style' => 'two',                ]            ]        );        $this->add_control(            'image3',            [                'label' => esc_html__( 'Choose Image Two', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::MEDIA,                'default' => [                    'url' => \Elementor\Utils::get_placeholder_image_src(),                ],                'media_types' => [ 'image', 'svg' ],                'condition' => [                    'style' => 'two',                ],            ]        );		$this->add_control(			'description',			[				'label'       => esc_html__( 'Description', 'wphex-master' ),				'type'        => Controls_Manager::TEXTAREA,				'description' => esc_html__( 'enter text.', 'wphex-master' ),				'default'     => esc_html__('Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do','wphex-master'),                'condition' => [                    'style' => 'one',                ],			]		);        $this->add_control(            'image_width',            [                'label' => esc_html__( 'Width', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::SLIDER,                'size_units' => [ 'px', '%' ],                'range' => [                    'px' => [                        'min' => 0,                        'max' => 1000,                        'step' => 5,                    ],                    '%' => [                        'min' => 0,                        'max' => 100,                    ],                ],                'default' => [                    'unit' => '%',                    'size' => 100,                ],                'selectors' => [                    '{{WRAPPER}} .single_demo__thumb img' => 'width: {{SIZE}}{{UNIT}};',                ],                'condition' => [                    'style' => 'two',                ],            ]        );        $this->add_control(            'image_height',            [                'label' => esc_html__( 'Height', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::SLIDER,                'size_units' => [ 'px', '%' ],                'range' => [                    'px' => [                        'min' => 0,                        'max' => 1000,                        'step' => 5,                    ],                    '%' => [                        'min' => 0,                        'max' => 100,                    ],                ],                'selectors' => [                    '{{WRAPPER}} .single_demo__thumb img' => 'height: {{SIZE}}{{UNIT}};',                ],                'condition' => [                    'style' => 'two',                ],            ]        );		$this->end_controls_section();	}	/**	 * Render Elementor widget output on the frontend.	 *	 * Written in PHP and used to generate the final HTML.	 *	 * @since 1.0.0	 * @access protected	 */	protected function render() {		$settings = $this->get_settings_for_display();        $image_id = !empty($settings['image']['id']) ? $settings['image']['id'] : '';        $image_alt = !empty($image_id) ? get_post_meta($image_id,'_wp_attachment_image_alt',true) : '';        $image_one = ! empty( $settings['image']['url'] ) ? $settings['image']['url'] : '';        $image_id_two = !empty( $settings['image2']['id'] ) ? $settings['image2']['id'] : '';        $image_alt_two = !empty($image_id_two) ? get_post_meta($image_id_two,'_wp_attachment_image_alt',true) : '';        $image_two = ! empty( $settings['image2']['url'] ) ? $settings['image2']['url'] : '';        $image_id_two = !empty( $settings['image3']['id'] ) ? $settings['image3']['id'] : '';        $image_alt_three = !empty($image_id_two) ? get_post_meta($image_id_two,'_wp_attachment_image_alt',true) : '';        $image_three = ! empty( $settings['image3']['url'] ) ? $settings['image3']['url'] : '';        $this->add_render_attribute('anchor_link','class','');        if (!empty($settings['url']['url'])){            $this->add_link_attributes('anchor_link',$settings['url']);        }		?>        <?php if( 'one' == $settings['style'] ) { ?>        <div id="block1" class="single-image-box-01">            <div class="thumb">	            <img src="<?php echo esc_url($image_one);?>" alt="<?php echo esc_attr($image_alt); ?>">            </div>            <div class="content">                <a ><h4 class="title"><?php echo esc_html__( $settings['title'] )?></h4></a>                <p><?php echo esc_html__($settings['description'])?></p>            </div>        </div>        <?php } ?>        <?php if( 'two' == $settings['style'] ) { ?>        <div id="block2" class="app_wrapper">            <div class="app_mobile">                <div class="app_mobile__inner">                    <div class="app_mobile_thumb wow fadeInLeft" data-wow-delay=".1s">                        <img src="<?php echo esc_url($image_one);?>" alt="<?php echo esc_attr($image_alt); ?>">                    </div>                    <div class="app_mobile_thumb right wow fadeInUp" data-wow-delay=".3s">                        <img src="<?php echo esc_url($image_two);?>" alt="<?php echo esc_attr($image_alt_two); ?>">                    </div>                    <div class="app_mobile_thumb right wow fadeInRight" data-wow-delay=".1s">                        <img src="<?php echo esc_url($image_three);?>" alt="<?php echo esc_attr($image_alt_three); ?>">                    </div>                </div>            </div>        </div>        <?php } ?>        <?php if( 'three' == $settings['style'] ) { ?>        <div id="block3" class="single_demo">            <div class="single_demo__thumb">                <a href=""><img src="<?php echo esc_url($image_one);?>" alt="<?php echo esc_attr($image_alt);?>"></a>            </div>        </div>        <?php } ?>		<?php	}}Plugin::instance()->widgets_manager->register_widget_type( new wphex_Image_Box_Five_Widget() );