<?php/** * Elementor Widget * @package wphex * @since 1.0.0 */namespace Elementor;class wphex_Navigation_Menu_Widget extends Widget_Base {    /**     * Get widget name.     *     * Retrieve Elementor widget name.     *     * @return string Widget name.     * @since 1.0.0     * @access public     *     */    public function get_name() {        return 'wphex-navigation-menu-widget';    }    /**     * Get widget title.     *     * Retrieve Elementor widget title.     *     * @return string Widget title.     * @since 1.0.0     * @access public     *     */    public function get_title() {        return esc_html__( 'WPHex Navigation Menu', 'wphex-master' );    }    /**     * Get widget icon.     *     * Retrieve Elementor widget icon.     *     * @return string Widget icon.     * @since 1.0.0     * @access public     *     */    public function get_icon() {        return 'eicon-wordpress';    }    /**     * Get widget categories.     *     * Retrieve the list of categories the Elementor widget belongs to.     *     * @return array Widget categories.     * @since 1.0.0     * @access public     *     */    public function get_categories() {        return [ 'wordpress' ];    }    /**     * Register Elementor widget controls.     *     * Adds different input fields to allow the user to change and customize the widget settings.     *     * @since 1.0.0     * @access protected     */    protected function _register_controls() {        $this->start_controls_section(            'search_content',            [                'label' => esc_html__( 'Navigation Menu Content', 'wphex-master' ),                'tab'   => Controls_Manager::TAB_CONTENT,            ]        );        $this->add_control(            'menu_title',            [                'label' => esc_html__( 'Title', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::TEXT,                'default' => esc_html__( 'Nav Title', 'wphex-master' ),                'placeholder' => esc_html__( 'Type your title here', 'wphex-master' ),            ]        );        $this->add_control(            'menu_name',            [                'label' => esc_html__( 'Select Menu', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::SELECT,                'default' => '',                'options' => $this->all_nav_menus(),            ]        );        $this->add_control(            'title_margin_bottom',            [                'label' => esc_html__( 'Title Margin Bottom', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::SLIDER,                'size_units' => [ 'px', 'rem' ],                'range' => [                    'px' => [                        'min' => 0,                        'max' => 1000,                        'step' => 5,                    ],                ],                'default' => [                    'unit' => 'rem',                    'size' => 1.5,                ],                'selectors' => [                    '{{WRAPPER}} .footer-widget-title.footer-nav-menu' => 'margin-bottom: {{SIZE}}{{UNIT}};',                ],            ]        );        $this->end_controls_section();    }    public function all_nav_menus() {        $all_menus = wp_get_nav_menus();        $final_menu = [];        $final_menu[''] = esc_html__( 'Select A Menu','irt' );        foreach( $all_menus as $menu ) {            $final_menu[ $menu->slug ] = $menu->name;        }        return $final_menu;    }    /**     * Render Elementor widget output on the frontend.     *     * Written in PHP and used to generate the final HTML.     *     * @since 1.0.0     * @access protected     */    protected function render() {        $settings = $this->get_settings_for_display();        $menu = ! empty( $settings['menu_name'] ) ? $settings['menu_name'] : '';        $args = [            'menu' => $menu,            'menu_class' => 'footer-widget-link-list',            'container_class' => 'footer-widget-inner',            'add_li_class'  => 'footer-widget-link-list-item'        ];        ?>        <h4 class="footer-widget-title footer-nav-menu"><?php echo esc_html( $settings['menu_title'] ); ?></h4>        <?php echo wp_nav_menu( $args ); ?>        <?php    }}Plugin::instance()->widgets_manager->register_widget_type( new wphex_Navigation_Menu_Widget() );