<?php/** * Elementor Widget * @package Appside * @since 1.0.0 */namespace Elementor;class WPHex_Product_Features extends Widget_Base {    /**     * Get widget name.     *     * Retrieve Elementor widget name.     *     * @return string Widget name.     * @since 1.0.0     * @access public     *     */    public function get_name() {        return 'wphex-product-features-list-widget';    }    /**     * Get widget title.     *     * Retrieve Elementor widget title.     *     * @return string Widget title.     * @since 1.0.0     * @access public     *     */    public function get_title() {        return esc_html__( 'WPHex Product Features', 'wphex-master' );    }    /**     * Get widget icon.     *     * Retrieve Elementor widget icon.     *     * @return string Widget icon.     * @since 1.0.0     * @access public     *     */    public function get_icon() {        return 'eicon-editor-list-ul';    }    /**     * Get widget categories.     *     * Retrieve the list of categories the Elementor widget belongs to.     *     * @return array Widget categories.     * @since 1.0.0     * @access public     *     */    public function get_categories() {        return [ 'wphex_widgets' ];    }    /**     * Register Elementor widget controls.     *     * Adds different input fields to allow the user to change and customize the widget settings.     *     * @since 1.0.0     * @access protected     */    protected function _register_controls() {        // product features content section starts here        $this->start_controls_section(            'product_features_content',            [                'label' => esc_html__( 'Product Features Content', 'wphex-master' ),                'tab'   => Controls_Manager::TAB_CONTENT,            ]        );        $this->add_control(            'product_features_title',            [                'label' => esc_html__( 'Title', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::TEXT,                'default' => esc_html__( 'A Trusted Hub for WordPress Plugins and Themes', 'wphex-master' ),                'placeholder' => esc_html__( 'Type your title here', 'wphex-master' ),            ]        );        $this->add_control(            'product_features_description',            [                'label' => esc_html__( 'Description', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::TEXTAREA,                'rows' => 10,                'default' => esc_html__( 'Discover a curated marketplace where you can confidently find and access a wide range of high-quality WordPress plugins and themes. We handpick each offering, ensuring they meet stringent standards for reliability, functionality, and security.', 'wphex-master' ),                'placeholder' => esc_html__( 'Type your description here', 'wphex-master' ),            ]        );        $this->add_control(            'product_features_button_text',            [                'label' => esc_html__( 'Button Text', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::TEXT,                'default' => esc_html__( 'View all Plugins', 'wphex-master' ),                'placeholder' => esc_html__( 'Type button text here', 'wphex-master' ),            ]        );        $this->add_control(            'product_features_button_link',            [                'label' => esc_html__( 'Button Link', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::URL,                'placeholder' => esc_html__( 'https://your-link.com', 'wphex-master' ),                'options' => [ 'url', 'is_external', 'nofollow' ],                'default' => [                    'url' => '#',                    'is_external' => true,                    'nofollow' => true,                    // 'custom_attributes' => '',                ],                'label_block' => true,            ]        );                $repeater = new \Elementor\Repeater();        $repeater->add_control(			'features_title', [			 'label' => esc_html__( 'Title', 'wphex-master' ),            'type' => \Elementor\Controls_Manager::TEXT,            'default' => esc_html__( 'Features List Title.' , 'wphex-master' ),            'label_block' => true,			]		);        $this->add_control(            'features_list',            [                'label' => esc_html__( 'Features List', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::REPEATER,                'fields' => $repeater->get_controls(),                'default' => [                    [                        'features_title' => esc_html__( 'Lighting Fast Support', 'wphex-master' ),                    ],                    [                        'features_title' => esc_html__( 'Money Back Guarantee', 'wphex-master' ),                    ]                ],            ]        );        $this->end_controls_section();        // product features content section ends here    }    /**     * Render Elementor widget output on the frontend.     *     * Written in PHP and used to generate the final HTML.     *     * @since 1.0.0     * @access protected     */    protected function render() {        $settings = $this->get_settings_for_display();        $button_link = ! empty( $settings['product_features_button_link']['url'] ) ? $settings['product_features_button_link']['url'] : '';        ?>        <div class="wpHex_experience__wrap">            <div class="wphex_section__title text-left ">                <h2 class="title"><?php printf( esc_html__( '%s', 'wphex-master' ), $settings['product_features_title'] );?></h2>                <p class="wphex_section__para mt-4"><?php printf( esc_html__( '%s', 'wphex-master' ), $settings['product_features_description'] );?> </p>                <div class="wpHex_experience__wrap__trust mt-4">                    <div class="wpHex_experience__wrap__trust__flex">                        <?php if ( $settings['features_list'] ) : foreach ( $settings['features_list'] as $list_single ) : ?>                        <div class="wpHex_experience__wrap__trust__item">                            <div class="wpHex_experience__wrap__trust__item__icon"><i class="las la-bolt"></i></div>                            <h6 class="wpHex_experience__wrap__trust__item__title"><?php printf( esc_html__( '%s', 'wphex-master' ), $list_single['features_title'] )?></h6>                        </div>                        <?php endforeach; endif; ?>                    </div>                </div>                <?php if(!empty($button_link) && !empty($settings['product_features_button_text'])):?>                <div class="btn_wrapper mt-4">                    <a href="<?php echo esc_attr( $button_link ); ?>" class="wphex_cmn_btn btn_outline_1 btn_small color_1 radius-5"> <?php printf( esc_html__( '%s', 'wphex-master' ), $settings['product_features_button_text'] );?></a>                </div>                <?php endif;?>            </div>        </div>        <?php    }}Plugin::instance()->widgets_manager->register_widget_type( new WPHex_Product_Features() );