<?php/** * Elementor Widget * @package Appside * @since 1.0.0 */namespace Elementor;class wphex_Scroll_To_Section_Breadcrumb extends Widget_Base {    /**     * Get widget name.     *     * Retrieve Elementor widget name.     *     * @return string Widget name.     * @since 1.0.0     * @access public     *     */    public function get_name() {        return 'wphex-scroll-to-section-widget';    }    /**     * Get widget title.     *     * Retrieve Elementor widget title.     *     * @return string Widget title.     * @since 1.0.0     * @access public     *     */    public function get_title() {        return esc_html__( 'wphex Scroll To Section', 'wphex-master' );    }    /**     * Get widget icon.     *     * Retrieve Elementor widget icon.     *     * @return string Widget icon.     * @since 1.0.0     * @access public     *     */    public function get_icon() {        return 'eicon-scroll';    }    /**     * Get widget categories.     *     * Retrieve the list of categories the Elementor widget belongs to.     *     * @return array Widget categories.     * @since 1.0.0     * @access public     *     */    public function get_categories() {        return [ 'wphex_widgets' ];    }    /**     * Register Elementor widget controls.     *     * Adds different input fields to allow the user to change and customize the widget settings.     *     * @since 1.0.0     * @access protected     */    protected function _register_controls() {        // product features content section starts here        $this->start_controls_section(            'breadcumb_content',            [                'label' => esc_html__( 'Content', 'wphex-master' ),                'tab'   => Controls_Manager::TAB_CONTENT,            ]        );        $this->add_control(            'breadcumb_list',            [                'label' => esc_html__( 'Breadcrumb Menu List', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::REPEATER,                'fields' => [                    [                        'name' => 'menu_title',                        'label' => esc_html__( 'Title', 'wphex-master' ),                        'type' => \Elementor\Controls_Manager::TEXT,                        'default' => esc_html__( 'Menu Title' , 'wphex-master' ),                        'label_block' => true,                    ],                    [                        'name' => 'menu_id',                        'label' => esc_html__( 'Id', 'wphex-master' ),                        'type' => \Elementor\Controls_Manager::TEXT,                        'default' => esc_html__( 'id' , 'wphex-master' ),                        'label_block' => true,                    ],                ],                'default' => [                    [                        'menu_title' => esc_html__( 'Payment Gateway', 'wphex-master' ),                        'menu_id' => esc_html__( 'id', 'wphex-master' ),                    ],                    [                        'menu_title' => esc_html__( 'Payment Gateway', 'wphex-master' ),                        'menu_id' => esc_html__( 'id', 'wphex-master' ),                    ],                    [                        'menu_title' => esc_html__( 'Payment Gateway', 'wphex-master' ),                        'menu_id' => esc_html__( 'id', 'wphex-master' ),                    ],                    [                        'menu_title' => esc_html__( 'Payment Gateway', 'wphex-master' ),                        'menu_id' => esc_html__( 'id', 'wphex-master' ),                    ],                ],            ]        );        $this->add_control(            'show_button',            [                'label' => esc_html__( 'Show Button', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::SWITCHER,                'label_on' => esc_html__( 'Show', 'wphex-master' ),                'label_off' => esc_html__( 'Hide', 'wphex-master' ),                'return_value' => 'yes',                'default' => 'yes',            ]        );        $this->add_control(            'button_text',            [                'label' => esc_html__( 'Button Text', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::TEXT,                'default' => esc_html__( 'Purchase Now', 'wphex-master' ),                'placeholder' => esc_html__( 'Type button text here', 'wphex-master' ),                'condition' => [                        'show_button' => 'yes'                ]            ]        );        $this->add_control(            'button_link',            [                'label' => esc_html__( 'Button Link', 'wphex-master' ),                'type' => \Elementor\Controls_Manager::URL,                'placeholder' => esc_html__( 'https://your-link.com', 'wphex-master' ),                'options' => [ 'url', 'is_external', 'nofollow' ],                'default' => [                    'url' => '#',                    'is_external' => true,                    'nofollow' => true,                    // 'custom_attributes' => '',                ],                'label_block' => true,                'condition' => [                    'show_button' => 'yes'                ]            ]        );        $this->end_controls_section();        // product features content section ends here    }    /**     * Render Elementor widget output on the frontend.     *     * Written in PHP and used to generate the final HTML.     *     * @since 1.0.0     * @access protected     */    protected function render() {        $settings = $this->get_settings_for_display();        $button_link = ! empty( $settings['button_link']['url'] ) ? $settings['button_link']['url'] : '';        ?>        <div id="navigation" class="breadcrumb_nav nav-absolute">            <div class="container">                <div class="breadcrumb_nav__flex">                    <div class="breadcrumb_nav_wrap">                        <ul class="breadcrumb_nav__list">                            <?php if( ! empty( $settings['breadcumb_list'] ) ) : foreach( $settings['breadcumb_list'] as $single_item ) : ?>                            <li class="breadcrumb_nav__list__item">                                <a href="#<?php echo esc_attr( $single_item['menu_id'] ); ?>" class="breadcrumb_nav__list__link"><?php echo esc_html( $single_item['menu_title'] ); ?></a>                            </li>                            <?php endforeach; endif; ?>                        </ul>                    </div>                    <div class="breadcrumb_nav__right">                        <div class="breadcrumb_nav__right__item <?php if( 'yes' !== $settings['show_button'] ) { echo esc_attr( 'scroll-to-top-btn-hide' ); } ?>">                            <a href="<?php echo esc_url( $button_link ); ?>" class="cmn-btn btn-gradient-1"><?php echo esc_html( $settings['button_text'] ); ?></a>                        </div>                    </div>                </div>            </div>        </div>        <?php    }}Plugin::instance()->widgets_manager->register_widget_type( new wphex_Scroll_To_Section_Breadcrumb() );