<?php/** * Elementor Widget * @package wphex * @since 1.0.0 */namespace Elementor;class wphex_WP_Search_Widget extends Widget_Base {    /**     * Get widget name.     *     * Retrieve Elementor widget name.     *     * @return string Widget name.     * @since 1.0.0     * @access public     *     */    public function get_name() {        return 'wphex-wp-search-widget';    }    /**     * Get widget title.     *     * Retrieve Elementor widget title.     *     * @return string Widget title.     * @since 1.0.0     * @access public     *     */    public function get_title() {        return esc_html__( 'wphex WP Search', 'wphex-master' );    }    /**     * Get widget icon.     *     * Retrieve Elementor widget icon.     *     * @return string Widget icon.     * @since 1.0.0     * @access public     *     */    public function get_icon() {        return 'eicon-wordpress';    }    /**     * Get widget categories.     *     * Retrieve the list of categories the Elementor widget belongs to.     *     * @return array Widget categories.     * @since 1.0.0     * @access public     *     */    public function get_categories() {        return [ 'wordpress' ];    }    /**     * Register Elementor widget controls.     *     * Adds different input fields to allow the user to change and customize the widget settings.     *     * @since 1.0.0     * @access protected     */    protected function _register_controls() {        $this->start_controls_section(            'search_content',            [                'label' => esc_html__( 'Search Contents', 'wphex-master' ),                'tab'   => Controls_Manager::TAB_CONTENT,            ]        );        $this->end_controls_section();    }    public function reg() {        add_filter( 'get_search_form', [ $this, 'wphex_wp_search' ] );    }    function wphex_wp_search ( $form ) {        $form = '<div class="blog_details__sidebar__item">                        <div class="blog_details__sidebar__search">                            <form method="get" action="' . esc_url(home_url('/')) . '">                                <input class="form--control" type="text" placeholder="' . esc_attr__('Search...', 'text-domain') . '" value="' . get_search_query() . '" name="s">                                <span class="blog_details__sidebar__search__icon"><i class="fa-solid fa-search"></i></span>                            </form>                        </div>                    </div>';        return $form;    }    /**     * Render Elementor widget output on the frontend.     *     * Written in PHP and used to generate the final HTML.     *     * @since 1.0.0     * @access protected     */    protected function render() {        $settings = $this->get_settings_for_display();        $this->reg();        get_search_form();    }}Plugin::instance()->widgets_manager->register_widget_type( new wphex_WP_Search_Widget() );